/**
 * Input Validation Module for n8n
 *
 * Validates incoming data and generates data_quality_warnings
 * to be included in orchestrator packages
 */

// ============================================================================
// VALIDATION FUNCTIONS
// ============================================================================

/**
 * Validates contact profile structure
 */
function validateContactProfile(profile) {
  const warnings = [];
  const errors = [];

  if (!profile) {
    errors.push('contact_info is missing or null');
    return { valid: false, warnings, errors };
  }

  if (!profile.full_name && !profile.name) {
    warnings.push('No name information available - personalization will be limited');
  }

  if (!profile.linkedin_url) {
    warnings.push('No LinkedIn URL - linkedin channel may be limited');
  }

  if (!profile.company) {
    warnings.push('No company information - business context analysis will be limited');
  }

  if (!profile.job) {
    warnings.push('No job title - role-based personalization unavailable');
  }

  return {
    valid: errors.length === 0,
    warnings,
    errors
  };
}

/**
 * Validates activities array
 */
function validateActivities(activities) {
  const warnings = [];
  const errors = [];
  const channelStats = {};

  if (!activities || !Array.isArray(activities)) {
    warnings.push('No activities found - analysis will be based on limited data');
    return { warnings, errors, channelStats, hasActivities: false };
  }

  if (activities.length === 0) {
    warnings.push('Activities array is empty - no interaction history available');
    return { warnings, errors, channelStats, hasActivities: false };
  }

  for (const activity of activities) {
    const type = activity.activity_type || 'UNKNOWN';
    channelStats[type] = (channelStats[type] || 0) + 1;

    if (!activity.recorded_on) {
      warnings.push(`Activity ${activity.activity_id || 'unknown'} missing recorded_on date`);
    }

    if (!activity.direction) {
      warnings.push(`Activity ${activity.activity_id || 'unknown'} missing direction`);
    }
  }

  return {
    warnings,
    errors,
    channelStats,
    hasActivities: true,
    totalActivities: activities.length
  };
}

/**
 * Validates available communication channels from contact info
 */
function validateAvailableChannels(profile) {
  const availableChannels = [];
  const warnings = [];

  if (!profile) {
    warnings.push('CRITICAL: No contact profile - cannot determine available channels');
    return { availableChannels, warnings };
  }

  if (profile.linkedin_url || profile.linkedin_urn) {
    availableChannels.push('linkedin');
  } else {
    warnings.push('No LinkedIn URL - linkedin channel unavailable');
  }

  if (profile.email) {
    availableChannels.push('email');
  }

  if (profile.whatsapp_number) {
    availableChannels.push('whatsapp');
  }

  if (profile.phone) {
    availableChannels.push('phone');
  }

  if (availableChannels.length === 0) {
    warnings.push('CRITICAL: No communication channels available - cannot send messages');
  }

  return {
    availableChannels,
    warnings
  };
}

/**
 * Validates stats object
 */
function validateStats(stats) {
  const warnings = [];
  const errors = [];

  if (!stats) {
    warnings.push('Stats object is missing');
    return { warnings, errors };
  }

  if (stats.total_activities === undefined) {
    warnings.push('total_activities count is missing from stats');
  }

  return { warnings, errors };
}

/**
 * Analyzes conversation patterns from activities
 */
function analyzeConversationPatterns(activities) {
  if (!activities || activities.length === 0) {
    return {
      lastInbound: null,
      lastOutbound: null,
      inboundCount: 0,
      outboundCount: 0,
      daysSinceLastContact: null,
      conversationState: 'no_history'
    };
  }

  let lastInbound = null;
  let lastOutbound = null;
  let inboundCount = 0;
  let outboundCount = 0;

  const sorted = [...activities].sort((a, b) => 
    new Date(b.recorded_on) - new Date(a.recorded_on)
  );

  for (const activity of sorted) {
    if (activity.direction === 'INBOUND') {
      inboundCount++;
      if (!lastInbound) lastInbound = activity;
    } else if (activity.direction === 'OUTBOUND') {
      outboundCount++;
      if (!lastOutbound) lastOutbound = activity;
    }
  }

  const lastActivity = sorted[0];
  const daysSinceLastContact = lastActivity 
    ? Math.floor((new Date() - new Date(lastActivity.recorded_on)) / (1000 * 60 * 60 * 24))
    : null;

  let conversationState = 'active';
  if (daysSinceLastContact > 30) {
    conversationState = 'stale';
  } else if (daysSinceLastContact > 90) {
    conversationState = 'dormant';
  }

  if (lastOutbound && (!lastInbound || new Date(lastOutbound.recorded_on) > new Date(lastInbound.recorded_on))) {
    conversationState = 'awaiting_response';
  }

  return {
    lastInbound,
    lastOutbound,
    inboundCount,
    outboundCount,
    daysSinceLastContact,
    conversationState
  };
}

// ============================================================================
// MAIN VALIDATION FUNCTION
// ============================================================================

/**
 * Comprehensive validation of input data
 */
function validateInputData(inputData) {
  const allWarnings = [];
  const allErrors = [];

  const contactInfo = inputData.contact_info || {};
  const activities = inputData.activities || [];
  const stats = inputData.stats || {};

  const profileValidation = validateContactProfile(contactInfo);
  allWarnings.push(...profileValidation.warnings);
  allErrors.push(...profileValidation.errors);

  const activitiesValidation = validateActivities(activities);
  allWarnings.push(...activitiesValidation.warnings);
  allErrors.push(...activitiesValidation.errors);

  const channelsValidation = validateAvailableChannels(contactInfo);
  allWarnings.push(...channelsValidation.warnings);

  const statsValidation = validateStats(stats);
  allWarnings.push(...statsValidation.warnings);
  allErrors.push(...statsValidation.errors);

  const conversationAnalysis = analyzeConversationPatterns(activities);

  const normalizedData = {
    ...inputData,
    _validation: {
      isValid: allErrors.length === 0,
      availableChannels: channelsValidation.availableChannels,
      activityStats: activitiesValidation.channelStats,
      hasActivities: activitiesValidation.hasActivities,
      totalActivities: activitiesValidation.totalActivities || 0
    },
    _conversationAnalysis: conversationAnalysis,
  };

  return {
    valid: allErrors.length === 0,
    normalizedData,
    data_quality_warnings: allWarnings,
    errors: allErrors,
    availableChannels: channelsValidation.availableChannels,
    conversationAnalysis
  };
}

// ============================================================================
// N8N EXECUTION
// ============================================================================

const items = $input.all();
const results = [];

for (const item of items) {
  const inputData = item.json;
  const validationResult = validateInputData(inputData);

  results.push({
    json: {
      contact_info: inputData.contact_info,
      activities: inputData.activities,
      stats: inputData.stats,
      validation: {
        is_valid: validationResult.valid,
        errors: validationResult.errors,
        data_quality_warnings: validationResult.data_quality_warnings,
        available_channels: validationResult.availableChannels
      },
      conversation_analysis: validationResult.conversationAnalysis,
      _normalized: validationResult.normalizedData
    }
  });
}

return results;

// Obtener el output raw del agente
const rawOutput = $input.first().json.output;

// Limpiar los backticks de markdown si existen
let cleanJson = rawOutput;
if (typeof rawOutput === 'string') {
  cleanJson = rawOutput
    .replace(/```json\n?/g, '')
    .replace(/```\n?/g, '')
    .trim();
}

// Parsear el JSON
let input;
try {
  input = typeof cleanJson === 'string' ? JSON.parse(cleanJson) : cleanJson;
} catch (e) {
  const jsonMatch = cleanJson.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    input = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('Could not parse Synthesis output: ' + e.message);
  }
}

const decision = input.final_decision;

if (!decision) {
  throw new Error('Missing final_decision in Synthesis output');
}

// Obtener datos del owner y contacto
let ownerName = '';
let contactLinkedinUrl = '';

// Intentar desde Webhook
try {
  const webhookData = $('Webhook').first().json;

  // LinkedIn del CONTACTO (de contact_info)
  contactLinkedinUrl = webhookData.body?.contact_info?.linkedin_url || '';

  // Nombre del OWNER (de la primera activity)
  const activities = webhookData.body?.activities || [];
  for (const activity of activities) {
    if (activity.owner && activity.owner.owner_name) {
      ownerName = activity.owner.owner_name;
      break;
    }
  }
} catch (e) {
  // Si falla Webhook, intentar desde Merge
  try {
    const mergeData = $('Merge').first().json;

    contactLinkedinUrl = mergeData.contact_info?.linkedin_url || '';

    const activities = mergeData.activities || [];
    for (const activity of activities) {
      if (activity.owner && activity.owner.owner_name) {
        ownerName = activity.owner.owner_name;
        break;
      }
    }
  } catch (e2) {
    console.log('Could not extract data from any source');
  }
}

return {
  json: {
    decision: {
      // Identificación
      orchestrator_id: input.orchestrator_id || 'Not specified',
      phase: input.phase || 'Not specified',
      contact_fullname: decision.contact_fullname || '',

      // Owner y Contact info
      owner_name: ownerName || 'Not specified',
      contact_linkedin_url: contactLinkedinUrl || 'Not specified',

      // Acción principal
      action: decision.action || 'Not specified',
      selected_channel: decision.selected_channel || 'Not specified',

      // Contenido seleccionado
      message: decision.selected_content?.message || 'No recommended message.',
      subject: decision.selected_content?.subject  || '',
      tone: decision.selected_content?.tone || '',
      content_source: decision.selected_content?.content_source || '',

      // Timing de ejecución
      execute_at: decision.execution_timing?.execute_at || 'Not specified',
      timezone: decision.execution_timing?.timezone || 'Not specified',
      timing_rationale: decision.execution_timing?.timing_rationale || 'Not specified',

      // Secuencia
      is_part_of_sequence: decision.sequence_instructions?.is_part_of_sequence || false,
      current_step: decision.sequence_instructions?.current_step || 1,
      total_steps: decision.sequence_instructions?.total_steps || 1,
      sequence_plan: decision.sequence_instructions?.sequence_plan || [],

      // Rationale
      overall_strategy: decision.rationale?.overall_strategy || '',
      channel_reasoning: decision.rationale?.channel_reasoning || '',
      content_reasoning: decision.rationale?.content_reasoning || '',
      timing_reasoning: decision.rationale?.timing_reasoning || '',
      sequence_reasoning: decision.rationale?.sequence_reasoning || '',

      // Confidence factors
      relationship_clarity: decision.rationale?.confidence_factors?.relationship_clarity || 0,
      opportunity_strength: decision.rationale?.confidence_factors?.opportunity_strength || 0,
      channel_certainty: decision.rationale?.confidence_factors?.channel_certainty || 0,
      content_quality: decision.rationale?.confidence_factors?.content_quality || 0,
      timing_accuracy: decision.rationale?.confidence_factors?.timing_accuracy || 0,

      // Riesgos y criterios
      risk_factors: decision.risk_factors || [],
      success_criteria_immediate: decision.success_criteria?.immediate || '',
      success_criteria_short_term: decision.success_criteria?.short_term || '',
      success_criteria_medium_term: decision.success_criteria?.medium_term || '',

      // Metadata
      synthesis_confidence: input.metadata?.synthesis_confidence || 0,
      agents_wave_1: input.metadata?.agents_consulted?.wave_1 || [],
      agents_wave_2: input.metadata?.agents_consulted?.wave_2 || [],
      iterations_total: input.metadata?.iterations_total || 0,
      processing_notes: input.metadata?.processing_notes || ''
    }
  }
};

// Parse Evaluation Decision - IMPROVED VERSION
const evaluationOutput = $input.first().json;
const constants = $('Constants').first().json;

let rawOutput = evaluationOutput.output || evaluationOutput.text || '';
let parsedDecision = null;
let action = 'continue';
let retryInstructions = null;
let parseError = null;

try {
  if (typeof rawOutput === 'string') {
    let cleanOutput = rawOutput.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
    const jsonMatch = cleanOutput.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      parsedDecision = JSON.parse(jsonMatch[0]);
    }
  } else if (typeof rawOutput === 'object' && rawOutput !== null) {
    parsedDecision = rawOutput;
  }

  if (parsedDecision) {
    const evalAction = parsedDecision.evaluation_decision?.action;
    if (evalAction === 'retry') {
      action = 'retry';
      retryInstructions = parsedDecision.evaluation_decision?.retry_instructions || null;
    } else {
      action = 'continue';
    }
  }
} catch (e) {
  parseError = e.message;
}

const currentIteration = constants.iteration_count || 1;
const maxIterations = constants.SEMANTIC_CONSTANTS?.MAX_RETRY_ITERATIONS || 2;

if (currentIteration >= maxIterations) {
  action = 'continue';
}

return {
  json: {
    decision: {
      action: action,
      current_iteration: currentIteration,
      max_iterations: maxIterations,
      should_retry: action === 'retry' && currentIteration < maxIterations,
      retry_instructions: retryInstructions
    },
    debug: {
      parse_error: parseError,
      found_action: parsedDecision?.evaluation_decision?.action || 'NOT_FOUND'
    },
    evaluation_output: evaluationOutput,
    wave1_results: $('Orchestrator Distribution').first().json,
    parsed_decision: parsedDecision
  }
};

// Prepare Retry Context
// Prepara el contexto para re-ejecutar Wave 1 con instrucciones de mejora

const input = $input.first().json;

// Obtener datos originales - buscar en múltiples fuentes posibles
let originalData;
try {
  // Intentar obtener del Input Validator (más confiable)
  originalData = $('Input Validator').first().json;
} catch (e) {
  // Fallback al Merge
  originalData = $('Merge').first().json;
}

let originalConstants;
try {
  originalConstants = $('Constants').first().json;
} catch (e) {
  originalConstants = {
    SEMANTIC_CONSTANTS: { MAX_RETRY_ITERATIONS: 2 },
    session_id: Date.now(),
    iteration_count: 1
  };
}

// Incrementar contador
const newIterationCount = (input.decision.current_iteration || 1) + 1;

// Construir el contexto de retry
const retryContext = {
  // Datos del contacto (desde Input Validator)
  contact_info: originalData.contact_info,
  activities: originalData.activities,
  stats: originalData.stats,
  validation: originalData.validation,
  conversation_analysis: originalData.conversation_analysis,
  _normalized: originalData._normalized,

  // Constants actualizados
  SEMANTIC_CONSTANTS: originalConstants.SEMANTIC_CONSTANTS,
  session_id: originalConstants.session_id,
  iteration_count: newIterationCount,

  // Meta de retry - CRÍTICO para que Distribution lo detecte
  retry_meta: {
    is_retry: true,
    iteration: newIterationCount,
    max_iterations: originalConstants.SEMANTIC_CONSTANTS?.MAX_RETRY_ITERATIONS || 2,
    previous_wave1_results: input.wave1_results?.output || {},
    retry_instructions: input.decision.retry_instructions || {},
    retry_reason: "Evaluation Orchestrator requested retry for improved analysis"
  }
};

return { json: retryContext };

